INSERT INTO socialnet.post_comment (id,"time",post_id,parent_id,author_id,comment_text,is_blocked) VALUES
	 (10000,'2023-01-07 23:52:33.514',10015,0,10003,'Но высокотехнологичная концепция общественного уклада требует анализа благоприятных перспектив.',false),
	 (10001,'2023-01-07 23:51:33.542',10001,0,10005,'С другой стороны, курс на социально-ориентированный национальный проект однозначно определяет каждого участника как способного принимать собственные решения касаемо позиций, занимаемых участниками в отношении поставленных задач.',false),
	 (10002,'2023-01-07 23:50:33.547',10011,0,10003,'Не следует, однако, забывать, что выбранный нами инновационный путь создаёт предпосылки для инновационных методов управления процессами.',false),
	 (10003,'2023-01-07 23:49:33.552',10010,0,10006,'Сложно сказать, почему стремящиеся вытеснить традиционное производство, нанотехнологии набирают популярность среди определенных слоев населения, а значит, должны быть объединены в целые кластеры себе подобных.',false),
	 (10004,'2023-01-07 23:48:33.556',10004,0,10006,'Задача организации, в особенности же глубокий уровень погружения играет важную роль в формировании форм воздействия.',false),
	 (10005,'2023-01-07 23:47:33.562',10006,0,10008,'Таким образом, семантический разбор внешних противодействий предполагает независимые способы реализации существующих финансовых и административных условий.',false),
	 (10006,'2023-01-07 23:46:33.567',10016,0,10008,'Современные технологии достигли такого уровня, что разбавленное изрядной долей эмпатии, рациональное мышление, в своём классическом представлении, допускает внедрение своевременного выполнения сверхзадачи.',false),
	 (10007,'2023-01-07 23:45:33.572',10005,0,10008,'Ясность нашей позиции очевидна: разбавленное изрядной долей эмпатии, рациональное мышление, а также свежий взгляд на привычные вещи — безусловно открывает новые горизонты для новых предложений.',false),
	 (10008,'2023-01-07 23:44:33.578',10001,0,10007,'Безусловно, высокое качество позиционных исследований предопределяет высокую востребованность прогресса профессионального сообщества.',false),
	 (10009,'2023-01-07 23:43:33.583',10002,0,10001,'Господа, курс на социально-ориентированный национальный проект в значительной степени обусловливает важность направлений прогрессивного развития!',false);
INSERT INTO socialnet.post_comment (id,"time",post_id,parent_id,author_id,comment_text,is_blocked) VALUES
	 (10010,'2023-01-07 23:42:33.588',10001,0,10009,'Есть над чем задуматься: реплицированные с зарубежных источников, современные исследования ассоциативно распределены по отраслям.',false),
	 (10011,'2023-01-07 23:41:33.593',10011,0,10002,'Принимая во внимание показатели успешности, постоянное информационно-пропагандистское обеспечение нашей деятельности однозначно определяет каждого участника как способного принимать собственные решения касаемо как самодостаточных, так и внешне зависимых концептуальных решений.',false),
	 (10012,'2023-01-07 23:40:33.597',10011,0,10003,'Есть над чем задуматься: диаграммы связей будут представлены в исключительно положительном свете.',false),
	 (10013,'2023-01-07 23:39:33.606',10018,0,10005,'Равным образом, понимание сути ресурсосберегающих технологий способствует подготовке и реализации экономической целесообразности принимаемых решений.',false),
	 (10014,'2023-01-07 23:38:33.611',10013,0,10001,'Задача организации, в особенности же укрепление и развитие внутренней структуры однозначно фиксирует необходимость новых принципов формирования материально-технической и кадровой базы.',false),
	 (10015,'2023-01-07 23:37:33.616',10007,0,10007,'А также сторонники тоталитаризма в науке могут быть смешаны с не уникальными данными до степени совершенной неузнаваемости, из-за чего возрастает их статус бесполезности.',false),
	 (10016,'2023-01-07 23:36:33.624',10017,0,10008,'Следует отметить, что социально-экономическое развитие в значительной степени обусловливает важность глубокомысленных рассуждений.',false),
	 (10017,'2023-01-07 23:35:33.629',10014,0,10007,'Значимость этих проблем настолько очевидна, что граница обучения кадров говорит о возможностях существующих финансовых и административных условий.',false),
	 (10018,'2023-01-07 23:34:33.635',10011,0,10000,'Однозначно, стремящиеся вытеснить традиционное производство, нанотехнологии описаны максимально подробно.',false),
	 (10019,'2023-01-07 23:33:33.640',10019,0,10004,'В целом, конечно, сплочённость команды профессионалов, в своём классическом представлении, допускает внедрение существующих финансовых и административных условий.',false);
INSERT INTO socialnet.post_comment (id,"time",post_id,parent_id,author_id,comment_text,is_blocked) VALUES
	 (10020,'2023-01-12 23:32:33.647',10004,10004,10008,'Банальные, но неопровержимые выводы, а также активно развивающиеся страны третьего мира подвергнуты целой серии независимых исследований.',false),
	 (10021,'2023-01-12 23:31:33.660',10011,10002,10003,'Принимая во внимание показатели успешности, укрепление и развитие внутренней структуры создаёт предпосылки для существующих финансовых и административных условий.',false),
	 (10022,'2023-01-12 23:30:33.667',10011,10012,10000,'В своём стремлении повысить качество жизни, они забывают, что выбранный нами инновационный путь однозначно фиксирует необходимость инновационных методов управления процессами.',false),
	 (10023,'2023-01-12 23:29:33.677',10018,10013,10006,'Прежде всего, синтетическое тестирование способствует повышению качества направлений прогрессивного развития.',false),
	 (10024,'2023-01-12 23:28:33.681',10011,10012,10004,'Разнообразный и богатый опыт говорит нам, что глубокий уровень погружения обеспечивает актуальность благоприятных перспектив.',false),
	 (10025,'2023-01-12 23:27:33.687',10015,10000,10006,'Противоположная точка зрения подразумевает, что предприниматели в сети интернет, инициированные исключительно синтетически, объявлены нарушающими общечеловеческие нормы этики и морали.',false),
	 (10026,'2023-01-12 23:26:33.690',10011,10002,10009,'Противоположная точка зрения подразумевает, что явные признаки победы институционализации освещают чрезвычайно интересные особенности картины в целом, однако конкретные выводы, разумеется, представлены в исключительно положительном свете.',false),
	 (10027,'2023-01-12 23:25:33.697',10015,10000,10006,'Мы вынуждены отталкиваться от того, что базовый вектор развития требует определения и уточнения переосмысления внешнеэкономических политик.',false),
	 (10028,'2023-01-12 23:24:33.700',10006,10005,10000,'Каждый из нас понимает очевидную вещь: экономическая повестка сегодняшнего дня способствует повышению качества анализа существующих паттернов поведения.',false),
	 (10029,'2023-01-12 23:23:33.706',10015,10000,10002,'Имеется спорная точка зрения, гласящая примерно следующее: многие известные личности могут быть смешаны с не уникальными данными до степени совершенной неузнаваемости, из-за чего возрастает их статус бесполезности.',false);
INSERT INTO socialnet.post_comment (id,"time",post_id,parent_id,author_id,comment_text,is_blocked) VALUES
	 (10030,'2023-01-12 23:22:33.710',10011,10012,10005,'Предварительные выводы неутешительны: высокое качество позиционных исследований способствует повышению качества прогресса профессионального сообщества!',false),
	 (10031,'2023-01-12 23:21:33.721',10011,10012,10001,'Являясь всего лишь частью общей картины, активно развивающиеся страны третьего мира объединены в целые кластеры себе подобных.',false),
	 (10032,'2023-01-12 23:20:33.725',10004,10004,10003,'Следует отметить, что консультация с широким активом позволяет оценить значение прогресса профессионального сообщества.',false),
	 (10033,'2023-01-12 23:19:33.730',10011,10011,10004,'Но сторонники тоталитаризма в науке формируют глобальную экономическую сеть и при этом — ограничены исключительно образом мышления.',false),
	 (10034,'2023-01-12 23:18:33.734',10004,10004,10002,'Лишь непосредственные участники технического прогресса набирают популярность среди определенных слоев населения, а значит, должны быть в равной степени предоставлены сами себе.',false),
	 (10035,'2023-01-12 23:17:33.738',10011,10018,10007,'Противоположная точка зрения подразумевает, что ключевые особенности структуры проекта лишь добавляют фракционных разногласий и призваны к ответу.',false),
	 (10036,'2023-01-12 23:16:33.742',10018,10013,10008,'Банальные, но неопровержимые выводы, а также ключевые особенности структуры проекта, вне зависимости от их уровня, должны быть рассмотрены исключительно в разрезе маркетинговых и финансовых предпосылок.',false),
	 (10037,'2023-01-12 23:15:33.749',10011,10011,10001,'Современные технологии достигли такого уровня, что сплочённость команды профессионалов выявляет срочную потребность дальнейших направлений развития.',false),
	 (10038,'2023-01-12 23:14:33.752',10001,10001,10000,'Вот вам яркий пример современных тенденций — высокое качество позиционных исследований влечет за собой процесс внедрения и модернизации новых принципов формирования материально-технической и кадровой базы.',false),
	 (10039,'2023-01-12 23:13:33.757',10011,10012,10006,'Таким образом, современная методология разработки однозначно фиксирует необходимость модели развития.',false);
